import React, { useState, useEffect } from 'react';

function MatrixForm({ onSubmit }) {
    const [formData, setFormData] = useState({
        mode: 'compare',
        size: 500,
        threads: 4
    });

    const [algorithms, setAlgorithms] = useState([
        { id: 'compare', name: 'Compare All Algorithms' },
        { id: 'sequential', name: 'Sequential Algorithm' },
        { id: 'basic_parallel', name: 'Basic Parallel Algorithm' },
        { id: 'improved_parallel', name: 'Improved Parallel Algorithm' },
        { id: 'block_based', name: 'Block-Based Parallel Algorithm' }
    ]);

    useEffect(() => {
        // API'den algoritma listesini al
        fetch('/api/matrix/algorithms')
            .then(response => response.json())
            .then(data => {
                if (data && data.length > 0) {
                    // Karşılaştırma seçeneğini başa ekle
                    setAlgorithms([
                        { id: 'compare', name: 'Tüm Algoritmaları Karşılaştır' },
                        ...data
                    ]);
                }
            })
            .catch(error => console.error('Algoritma listesi alınamadı:', error));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;

        if (name === 'size') {
            // Matris boyutu için sınırlamalar
            const size = parseInt(value);
            if (size < 500) return; // Çok küçük matrisler için hesaplama anlamlı değil
            if (size > 1500) return; // Çok büyük matrisler çok fazla bellek kullanabilir

            setFormData({
                ...formData,
                [name]: size
            });
        } else if (name === 'threads') {
            // Thread sayısı için sınırlamalar
            const threads = parseInt(value);
            if (threads < 1) return;
            if (threads > 16) return; // Maksimum thread sayısı

            setFormData({
                ...formData,
                [name]: threads
            });
        } else {
            setFormData({
                ...formData,
                [name]: value
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <form onSubmit={handleSubmit}>
            <div className="mb-2">
                <label htmlFor="mode" className="form-label mb-1 fw-bold small">Algoritma Seçimi</label>
                <select
                    id="mode"
                    name="mode"
                    className="form-select form-select-sm"
                    value={formData.mode}
                    onChange={handleChange}
                >
                    {algorithms.map(alg => (
                        <option key={alg.id} value={alg.id}>
                            {alg.name}
                        </option>
                    ))}
                </select>
            </div>

            <div className="mb-2">
                <label htmlFor="size" className="form-label mb-1 fw-bold small">
                    Matris Boyutu: {formData.size}x{formData.size}
                </label>
                <input
                    type="range"
                    className="form-range"
                    id="size"
                    name="size"
                    min="500"
                    max="1500"
                    step="100"
                    value={formData.size}
                    onChange={handleChange}
                />
                <div className="d-flex justify-content-between small">
                    <small>500</small>
                    <small>1000</small>
                    <small>1500</small>
                </div>
            </div>

            <div className="mb-2">
                <label htmlFor="threads" className="form-label mb-1 fw-bold small">
                    Thread Sayısı: {formData.threads}
                </label>
                <input
                    type="range"
                    className="form-range"
                    id="threads"
                    name="threads"
                    min="1"
                    max="16"
                    value={formData.threads}
                    onChange={handleChange}
                    disabled={formData.mode === 'sequential' || formData.mode === 'block_based' || formData.mode === 'compare'}
                />
                <div className="d-flex justify-content-between small">
                    <small>1</small>
                    <small>8</small>
                    <small>16</small>
                </div>
            </div>

            <div className="alert alert-info py-2 small mb-2">
                <strong>Bilgi:</strong> Büyük matris boyutları (1000+) hesaplama süresini belirgin şekilde artırabilir.
            </div>

            <button type="submit" className="btn btn-primary btn-sm w-100">
                {formData.mode === 'compare' ? 'Karşılaştır' : 'Hesapla'}
            </button>
        </form>
    );
}

export default MatrixForm;
