import React, { useState, useEffect } from 'react';
import './App.css';
import MatrixForm from './components/MatrixForm';
import PerformanceChart from './components/PerformanceChart';
import ResultTable from './components/ResultTable';
import ThreadComparisonChart from './components/ThreadComparisonChart';
import { XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, BarChart, Bar } from 'recharts';

function App() {
    const [results, setResults] = useState(null);
    const [pastResults, setPastResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [historicalResults, setHistoricalResults] = useState([]);

    // Geçmiş sonuçlar değiştiğinde grafik verilerini güncelle
    useEffect(() => {
        // Geçmiş sonuçları karşılaştırma formatına dönüştür
        const formattedResults = pastResults.map(resultItem => {
            if (resultItem.data.sequential) {
                // Zaten karşılaştırma formatında
                return {
                    ...resultItem.data,
                    id: resultItem.id,
                    timestamp: resultItem.timestamp,
                    label: `Karşılaştırma (${resultItem.timestamp})`
                };
            } else {
                // Tek algoritma sonucunu karşılaştırma formatına dönüştür
                const converted = convertToComparisonFormat(resultItem.data);
                return {
                    ...converted,
                    id: resultItem.id,
                    timestamp: resultItem.timestamp,
                    label: `${resultItem.data.algorithm.toUpperCase()} (${resultItem.timestamp})`
                };
            }
        });

        setHistoricalResults(formattedResults);
    }, [pastResults]);

    // Zaman değerlerini formatlamak için yardımcı fonksiyon
    const formatTime = (value) => value.toFixed(6);

    // Tek algoritma sonucunu karşılaştırma formatına dönüştüren fonksiyon
    const convertToComparisonFormat = (singleResult) => {
        // Base format - tüm değerleri sıfırla
        const comparisonData = {
            matrixSize: singleResult.matrixSize,
            availableThreads: singleResult.availableThreads || singleResult.threads || 4,
            sequential: { time: 0, speedup: 1 },
            parallel: { time: 0, speedup: 0 },
            improved: { time: 0, speedup: 0 },
            blocked: { time: 0, speedup: 0 }
        };

        // Sadece çalıştırılan algoritmanın değerlerini ayarla
        if (singleResult.algorithm === 'sequential') {
            comparisonData.sequential.time = singleResult.executionTime;
            comparisonData.sequential.speedup = 1; // Sequential için speedup her zaman 1
        }
        else if (singleResult.algorithm === 'parallel') {
            comparisonData.parallel.time = singleResult.executionTime;
            comparisonData.parallel.speedup = singleResult.speedup || 1;
            // Referans olarak sekansiyel süre hesapla
            comparisonData.sequential.time = singleResult.executionTime * singleResult.speedup;
        }
        else if (singleResult.algorithm === 'improved') {
            comparisonData.improved.time = singleResult.executionTime;
            comparisonData.improved.speedup = singleResult.speedup || 1;
            // Referans olarak sekansiyel süre hesapla
            comparisonData.sequential.time = singleResult.executionTime * singleResult.speedup;
        }
        else if (singleResult.algorithm === 'blocked') {
            comparisonData.blocked.time = singleResult.executionTime;
            comparisonData.blocked.speedup = singleResult.speedup || 1;
            // Referans olarak sekansiyel süre hesapla
            comparisonData.sequential.time = singleResult.executionTime * singleResult.speedup;
        }

        return comparisonData;
    };

    // Tüm geçmiş sonuçları karşılaştırmak için grafik bileşeni
    const HistoricalComparisonChart = ({ data }) => {
        // Çalışma süresi verilerini hazırla
        const timeChartData = data.map(item => ({
            name: item.label,
            Sequential: item.sequential?.time || 0,
            'Basic Parallel': item.parallel?.time || 0,
            'Improved Parallel': item.improved?.time || 0,
            'Block-Based Parallel': item.blocked?.time || 0,
            matrixSize: item.matrixSize
        }));

        // Speedup verilerini hazırla
        const speedupChartData = data.map(item => ({
            name: item.label,
            Sequential: item.sequential?.speedup || 1,
            'Basic Parallel': item.parallel?.speedup || 0,
            'Improved Parallel': item.improved?.speedup || 0,
            'Block-Based Parallel': item.blocked?.speedup || 0,
            matrixSize: item.matrixSize
        }));

        return (
            <div className="chart-wrapper">
                <h5 className="text-center mb-3">Çalışma Süresi Karşılaştırması (Tüm Sonuçlar)</h5>
                <ResponsiveContainer width="100%" height={300}>
                    <BarChart
                        data={timeChartData}
                        margin={{
                            top: 5,
                            right: 30,
                            left: 20,
                            bottom: 120, // Etiketler için daha fazla alan
                        }}
                    >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis
                            dataKey="name"
                            angle={-45}
                            textAnchor="end"
                            height={100}
                            interval={0}
                        />
                        <YAxis />
                        <Tooltip
                            formatter={(value) => value.toFixed(6)}
                            labelFormatter={(label) => `${label} (${timeChartData.find(item => item.name === label)?.matrixSize}x${timeChartData.find(item => item.name === label)?.matrixSize})`}
                        />
                        <Legend />
                        <Bar dataKey="Sequential" fill="#6c757d" />
                        <Bar dataKey="Basic Parallel" fill="#0d6efd" />
                        <Bar dataKey="Improved Parallel" fill="#198754" />
                        <Bar dataKey="Block-Based Parallel" fill="#dc3545" />
                    </BarChart>
                </ResponsiveContainer>

                <h5 className="text-center mb-3 mt-5">Hızlanma (Speedup) Karşılaştırması (Tüm Sonuçlar)</h5>
                <ResponsiveContainer width="100%" height={300}>
                    <BarChart
                        data={speedupChartData}
                        margin={{
                            top: 5,
                            right: 30,
                            left: 20,
                            bottom: 120, // Etiketler için daha fazla alan
                        }}
                    >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis
                            dataKey="name"
                            angle={-45}
                            textAnchor="end"
                            height={100}
                            interval={0}
                        />
                        <YAxis />
                        <Tooltip
                            formatter={(value) => value.toFixed(2)}
                            labelFormatter={(label) => `${label} (${speedupChartData.find(item => item.name === label)?.matrixSize}x${speedupChartData.find(item => item.name === label)?.matrixSize})`}
                        />
                        <Legend />
                        <Bar dataKey="Sequential" fill="#6c757d" />
                        <Bar dataKey="Basic Parallel" fill="#0d6efd" />
                        <Bar dataKey="Improved Parallel" fill="#198754" />
                        <Bar dataKey="Block-Based Parallel" fill="#dc3545" />
                    </BarChart>
                </ResponsiveContainer>
            </div>
        );
    };

    const handleSubmit = async (params) => {
        setLoading(true);
        setError(null);

        try {
            const response = await fetch('/api/matrix/run', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(params)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'API isteği başarısız oldu.');
            }

            const data = await response.json();
            setResults(data);
            // Sonuçları geçmiş sonuçlara ekle
            setPastResults(prev => [...prev, {
                id: Date.now(), // benzersiz ID için timestamp
                timestamp: new Date().toLocaleTimeString(),
                data: data
            }]);
        } catch (err) {
            console.error('Error:', err);
            setError(err.message || 'Bir hata oluştu.');
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteResult = (id) => {
        setPastResults(prev => prev.filter(result => result.id !== id));
    };

    // Geçmiş Sonuç Kartı Bileşeni
    const ResultCard = ({ result, onDelete }) => {
        const { data, timestamp, id } = result;

        return (
            <div className="card mb-3 result-card">
                <div className="card-header d-flex justify-content-between align-items-center">
                    <div>
                        <strong>
                            {data.sequential
                                ? "Karşılaştırma"
                                : `${data.algorithm.toUpperCase()} Algoritması`}
                        </strong>
                        <span className="text-muted ms-2 small">{timestamp}</span>
                    </div>
                    <button
                        className="btn btn-sm btn-outline-danger py-0 px-2"
                        onClick={() => onDelete(id)}
                    >
                        <i className="bi bi-trash"></i>
                    </button>
                </div>
                <div className="card-body">
                    {data.sequential ? (
                        <div>
                            <p className="mb-1">Matris Boyutu: {data.matrixSize} x {data.matrixSize}</p>
                            <p className="mb-1">Thread Sayısı: {data.availableThreads}</p>
                            <div className="row small text-muted">
                                <div className="col">
                                    <div>Sequential: {formatTime(data.sequential.time)} sn</div>
                                </div>
                                <div className="col">
                                    <div>Basic Parallel: {formatTime(data.parallel.time)} sn</div>
                                </div>
                                <div className="col">
                                    <div>Improved Parallel: {formatTime(data.improved.time)} sn</div>
                                </div>
                                <div className="col">
                                    <div>Block-Based Parallel: {formatTime(data.blocked.time)} sn</div>
                                </div>
                            </div>
                        </div>
                    ) : (
                        <div>
                            <p className="mb-1">Matris Boyutu: {data.matrixSize} x {data.matrixSize}</p>
                            <p className="mb-1">Çalışma Süresi: {formatTime(data.executionTime)} saniye</p>
                            <p className="mb-1">Hızlanma (Speedup): {data.speedup.toFixed(2)}x</p>
                            <p className="mb-0">Thread Sayısı: {data.threads || "Varsayılan"}</p>
                        </div>
                    )}
                </div>
            </div>
        );
    };

    return (
        <div className="container">
            <header className="text-center my-5">
                <h1 className="display-4">Paralel Matris Çarpımı Performans Analizi</h1>
                <p className="lead">
                    Matris çarpımı için sıralı ve paralel algoritmaların performans karşılaştırması
                </p>
            </header>

            <div className="row">
                <div className="col-md-5">
                    <div className="section">
                        <h2 className="title">Ayarlar</h2>
                        <MatrixForm onSubmit={handleSubmit} />
                    </div>

                    {pastResults.length > 0 && (
                        <div className="section mt-4">
                            <h2 className="title">Geçmiş Sonuçlar</h2>
                            <div className="past-results">
                                <div className="row">
                                    {pastResults.map(result => (
                                        <div className="col-md-6 col-lg-6 mb-3" key={result.id}>
                                            <ResultCard
                                                result={result}
                                                onDelete={handleDeleteResult}
                                            />
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    )}
                </div>

                <div className="col-md-7">
                    <div className="section">
                        <h2 className="title">Sonuçlar</h2>
                        {loading && (
                            <div className="loading">
                                <div className="spinner-border text-primary" role="status">
                                    <span className="visually-hidden">Yükleniyor...</span>
                                </div>
                                <span className="ms-2">Hesaplama yapılıyor...</span>
                            </div>
                        )}

                        {error && (
                            <div className="alert alert-danger" role="alert">
                                {error}
                            </div>
                        )}

                        {results && !loading && (
                            <div className="result-section">
                                {results.sequential && (
                                    <>
                                        <h3 className="subtitle">Algoritma Performansları</h3>
                                        <PerformanceChart data={results} />
                                        <ResultTable data={results} />

                                        {results.threadComparison && results.threadComparison.length > 0 && (
                                            <>
                                                <h3 className="subtitle mt-4">Thread Karşılaştırması</h3>
                                                <ThreadComparisonChart data={results.threadComparison} />
                                            </>
                                        )}
                                    </>
                                )}

                                {!results.sequential && (
                                    <>
                                        <div className="alert alert-info">
                                            <h4>{results.algorithm.toUpperCase()} algoritması çalıştırıldı</h4>
                                            <p>Matris Boyutu: {results.matrixSize} x {results.matrixSize}</p>
                                            <p>Çalışma Süresi: {formatTime(results.executionTime)} saniye</p>
                                            <p>Hızlanma (Speedup): {results.speedup ? results.speedup.toFixed(2) : '1.00'}x</p>
                                            <p>Thread Sayısı: {results.threads || "Varsayılan"}</p>
                                            <p className="small mt-2 text-danger">
                                                Not: En doğru karşılaştırma sonuçları için "Tüm Algoritmaları Karşılaştır" modunu kullanmanız önerilir.
                                            </p>
                                        </div>

                                        {/* Tek algoritma çalıştırıldığında da grafik göster */}
                                        <h3 className="subtitle mt-4">Performans Gösterimi</h3>
                                        <p className="small text-muted">
                                            (Bu gösterim, seçilen algoritmanın performansını göstermektedir. Gerçek karşılaştırma için "Tüm Algoritmaları Karşılaştır" modunu seçin.)
                                        </p>
                                        <PerformanceChart data={convertToComparisonFormat(results)} />
                                        <ResultTable data={convertToComparisonFormat(results)} />
                                    </>
                                )}

                                {/* Geçmiş tüm sonuçların karşılaştırması */}
                                {historicalResults.length > 1 && (
                                    <div className="mt-5">
                                        <h3 className="subtitle">Tüm Sonuçlar Karşılaştırması</h3>
                                        <p className="small text-muted">
                                            (Bu grafik, geçmiş tüm ölçüm sonuçlarının karşılaştırmasını göstermektedir.)
                                        </p>
                                        <HistoricalComparisonChart data={historicalResults} />
                                    </div>
                                )}
                            </div>
                        )}
                    </div>
                </div>
            </div>

            <footer className="text-center mt-5 mb-4 text-muted">
                <p>
                    Paralel Hesaplama Dersi Projesi &copy; {new Date().getFullYear()}
                </p>
            </footer>
        </div>
    );
}

export default App;
